package cn.prince.service.impl;

import java.util.List;

import javax.annotation.Resource;

import org.springframework.stereotype.Service;

import cn.prince.mapper.MenuMapper;
import cn.prince.pojo.Menu;
import cn.prince.service.MenuService;



@Service
public class MenuServiceImpl implements MenuService {
	@Resource
	MenuMapper menuMapper;

	@Override
	public List<Menu> findMenuById(int id) {
		// TODO Auto-generated method stub
		return menuMapper.findMenuById(id);
	}

	@Override
	public List<Menu> findMenuById1(int id1) {
		// TODO Auto-generated method stub
		return menuMapper.findMenuById1(id1);
	}
}
